import React from 'react';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import { Facebook, Twitter, Instagram } from '@mui/icons-material'; // Suponiendo que quieras estos íconos

export default function ProductInfoModal({ open, handleClose, producto }) {
    return (
        <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="product-info-dialog-title"
        >
            <DialogTitle id="product-info-dialog-title">{producto.nombre}</DialogTitle>

            <DialogContent>
                {/* Miniatura de la imagen del producto */}
                <img src={producto.imagenURL} alt={producto.nombre} style={{ maxWidth: '100%', height: 'auto' }} />

                {/* Precio real */}
                <Typography variant="h6" gutterBottom>
                    Precio: €{producto.precioReal}
                </Typography>

                {/* Puntos conseguidos */}
                <Typography variant="body1" gutterBottom>
                    Puntos conseguidos: {producto.puntos}
                </Typography>

                {/* Información sobre cómo conseguir el producto */}
                <Typography variant="body2" gutterBottom>
                    {producto.descuentos}
                </Typography>
                <a href={producto.url} target="_blank" rel="noopener noreferrer">
                    Enlace al Producto
                </a>

                {/* Enlaces para compartir en redes sociales */}
                <div>
                    <IconButton color="primary">
                        <Facebook />
                    </IconButton>
                    <IconButton color="primary">
                        <Twitter />
                    </IconButton>
                    <IconButton color="primary">
                        <Instagram />
                    </IconButton>
                </div>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleClose} color="primary">
                    Cerrar
                </Button>
            </DialogActions>
        </Dialog>
    );
}
