import React from 'react';
import './Puntuacion.css';

const Puntuacion = ({ puntos, productoActual, totalProductos }) => {
    const maxPuntosPosibles = (productoActual === 0) ? 100 : ((productoActual === 1) ? 0 : (productoActual - 1) * 20);

    const productoAMostrar = (productoActual === 0) ? totalProductos : productoActual;

    return (
        <div className="puntuacion">
            <span>Producto {productoAMostrar} de {totalProductos}</span>
            <div className="puntuacion-bar-container">
                <div className="puntuacion-bar-background">
                    <div className="puntuacion-bar-fill" style={{ width: `${(puntos / maxPuntosPosibles) * 100}%` }}></div>
                </div>
                <span>{puntos}/{maxPuntosPosibles} puntos</span>
            </div>
        </div>
    );
}

export default Puntuacion;
