import React from 'react';

const Puntuacion = ({ puntos, productoActual, totalProductos }) => {
  const maxPuntosPosibles = productoActual * 20;

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
      <span>Producto {productoActual} de {totalProductos}</span>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <div style={{ width: '150px', height: '10px', backgroundColor: '#e0e0e0', marginRight: '10px', position: 'relative' }}>
          <div style={{ width: `${(puntos / maxPuntosPosibles) * 100}%`, height: '10px', backgroundColor: '#4caf50' }}></div>
        </div>
        <span>{puntos}/{maxPuntosPosibles} puntos</span>
      </div>
    </div>
  );
}

export default Puntuacion;
