import React, { useState, useEffect } from 'react';
import './App.css';
import Producto from './components/Producto';
import PrecioSlider from './components/PrecioSlider';
import Header from './components/layout/Header';
import { comprobarYActualizarPuntos } from './utils/comprobar';
import { haJugadoElDia } from './utils/storage';
import { cargarProductosDelDia } from './utils/carga';

const App = () => {
  const [productoActual, setProductoActual] = useState(null);
  const [precioEstimado, setPrecioEstimado] = useState(0);
  const [mostrarResumen, setMostrarResumen] = useState(false); // Nuevo estado
  const fechaHoy = new Date().toISOString().slice(0, 10); // Formato: "YYYY-MM-DD"

  const cargarProductoPorIndice = (indice) => {
    cargarProductosDelDia().then(productos => {
      if (indice !== -1 && productos.length > indice) {
        setProductoActual(productos[indice]);
        setMostrarResumen(false); // Resetear la vista de resumen
      } else if (indice === -1) {
        console.log("El jugador ya ha jugado todos los productos del día. Mostrar resumen.");
        setProductoActual(null);
        setMostrarResumen(true);
      }
    });
  };

  useEffect(() => {
    const indiceProducto = haJugadoElDia(fechaHoy);
    cargarProductoPorIndice(indiceProducto);
  }, [fechaHoy]);

  const comprobarPrecio = () => {
    comprobarYActualizarPuntos(fechaHoy, precioEstimado, productoActual.precio);
    setMostrarResumen(true);
  };

  const irAlSiguienteProducto = () => {
    const nuevoIndice = haJugadoElDia(fechaHoy);
    cargarProductoPorIndice(nuevoIndice);
  };

  return (
    <div className="App">
      <Header />
      {productoActual && (
        <div>
          <Producto producto={productoActual} />
          <PrecioSlider
            precioEstimado={precioEstimado}
            setPrecioEstimado={setPrecioEstimado}
            precioReal={productoActual.precio}
            precioCorrecto={mostrarResumen ? productoActual.precio : null} // Añadir la marca si estamos en la pantalla de resumen
          />
          {mostrarResumen ? (
            <button onClick={irAlSiguienteProducto}>Siguiente Producto</button>
          ) : (
            <button onClick={comprobarPrecio}>Comprobar</button>
          )}
        </div>
      )}
      {!productoActual && mostrarResumen && (
        <div>
          {/* Aquí puedes mostrar el resumen final o el componente de resultados */}
          <button onClick={() => console.log('Mostrar resultados finales')}>Resultados</button>
        </div>
      )}
    </div>
  );
}

export default App;
