export const calcularPuntos = (precioReal, precioUsuario) => {
    const diferenciaPorcentual = Math.abs(precioReal - precioUsuario) / precioReal * 100;

    console.log('Diferencia Porcentual:', diferenciaPorcentual);  // Imprimir la diferencia porcentual

    if (diferenciaPorcentual === 0) {
        console.log('Puntos:', 20); // Imprimir los puntos antes de retornar
        return 20;
    } else if (diferenciaPorcentual <= 5) {
        console.log('Puntos:', 18); // Imprimir los puntos antes de retornar
        return 18;
    }

    // Calcula cuántos "segmentos" de 5% hay en la diferencia.
    const segmentos = Math.floor(diferenciaPorcentual / 5);

    // Resta 2 puntos por cada "segmento".
    const puntos = 18 - (2 * (segmentos - 1));

    console.log('Puntos:', puntos); // Imprimir los puntos antes de retornar

    return puntos > 1 ? puntos : 1;
}
