import React, { useState, useEffect } from 'react';
import { isToday, isYesterday, format, addDays } from 'date-fns';
import { resumenDiasJugados } from '../utils/storage';
import './CalendarioPage.css';

function Calendario() {
    const [days, setDays] = useState([]);
    const diasJugados = resumenDiasJugados();

    useEffect(() => {
        const startDay = new Date(2023, 8, 13);
        const today = new Date();

        const calendarDays = [];

        for (let day = startDay; isBefore(day, today) || isToday(day); day = addDays(day, 1)) {
            calendarDays.push(day);
        }

        setDays(calendarDays);
    }, []);

    return (
        <div className="calendar">
            {days.map((day, index) => {
                const formattedDate = format(day, 'yyyy-MM-dd');
                const jugado = diasJugados[formattedDate];
                return (
                    <button key={index} style={{ backgroundColor: jugado ? 'red' : 'green' }}>
                        {format(day, 'dd')}
                    </button>
                );
            })}
        </div>
    );
}

export default Calendario;
