import React, { useState, useEffect } from 'react';
import './App.css';
import Producto from './components/Producto';
import PrecioSlider from './components/PrecioSlider';
import BotonComprobar from './components/BotonComprobar';
import ResumenProducto from './components/ResumenProducto'; 
import { haJugadoElDia } from './utils/storage';
import { cargarProductosDelDia } from './utils/carga';
import Header from './components/layout/Header';

const App = () => {
  const [productoActual, setProductoActual] = useState(null);
  const [precioEstimado, setPrecioEstimado] = useState(0);
  const [mostrarResumen, setMostrarResumen] = useState(false); // Estado para mostrar el resumen
  const fechaHoy = new Date().toISOString().slice(0, 10); // Formato: "YYYY-MM-DD"
  const [indiceProductoActual, setIndiceProductoActual] = useState(null); // Reintroduce el estado

  useEffect(() => {
    const indiceProducto = haJugadoElDia(fechaHoy);

    cargarProductosDelDia().then(productos => {
      if (indiceProducto !== -1 && productos.length > indiceProducto) {
        setProductoActual(productos[indiceProducto]);
        setIndiceProductoActual(indiceProducto); // Actualiza el índice del producto actual
      } else if (indiceProducto === -1) {
        console.log("El jugador ya ha jugado todos los productos del día. Mostrar resumen.");
      }
    });

  }, [fechaHoy]);

  const navegarAResumen = (productoActual, precioEstimado, puntos) => {
    setMostrarResumen(true); 
  }

  return (
    <div className="App">
      <Header />
      {productoActual && !mostrarResumen ? (
        <div>
          <Producto producto={productoActual} />
          <PrecioSlider 
            precioEstimado={precioEstimado} 
            setPrecioEstimado={setPrecioEstimado} 
            precioReal={productoActual.precio} 
          />
          <BotonComprobar 
            fecha={fechaHoy}
            indiceProducto={indiceProductoActual} // Usa el estado índice
            precioEstimado={precioEstimado}
            productoActual={productoActual}
            navegarAResumen={navegarAResumen}
          />
        </div>
      ) : (
        <ResumenProducto 
          producto={productoActual}
          precioEstimado={precioEstimado}
        />
      )}
    </div>
  );
}

export default App;
