import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import MuiInput from '@mui/material/Input';
import EuroIcon from '@mui/icons-material/Euro';

const Input = styled(MuiInput)`
  width: 70px;
`;

function determinarRango(precio) {
    if (precio < 50) return { min: 0, max: 100, step: 0.1 };
    if (precio < 500) return { min: 0, max: 1000, step: 1 };
    return { min: 0, max: 2000, step: 10 };
}

export default function InputSlider(props) {
    const { precioReal = 0 } = props;
    const { min, max, step } = determinarRango(precioReal);
    const [value, setValue] = React.useState((min + max) / 2);

    const handleSliderChange = (event, newValue) => {
        setValue(newValue);
    };

    const handleInputChange = (event) => {
        let val = parseFloat(event.target.value);
        if (isNaN(val)) {
            val = 0;
        } else {
            val = Math.min(Math.max(val, min), max);
        }
        setValue(val);
    };

    return (
        <Box sx={{ width: 250, margin: '0 auto', textAlign: 'center' }}> {/* Alineaci√≥n al centro */}
            <Typography id="input-slider" gutterBottom>
                Precio
            </Typography>
            <Grid container spacing={2} alignItems="center">
                <Grid item>
                    <EuroIcon /> {/* Icono de euro */}
                </Grid>
                <Grid item xs>
                    <Slider
                        value={typeof value === 'number' ? value : 0}
                        onChange={handleSliderChange}
                        min={min}
                        max={max}
                        step={step}
                        aria-labelledby="input-slider"
                    />
                </Grid>
                <Grid item>
                    <Input
                        value={value.toFixed(2)} // Aseguramos dos decimales al mostrar el valor
                        size="small"
                        onChange={handleInputChange}
                        inputProps={{
                            step: step,
                            min: min,
                            max: max,
                            type: 'text',
                            'aria-labelledby': 'input-slider',
                        }}
                    />
                </Grid>
            </Grid>
        </Box>
    );
}
