import React from 'react';
import './ResumenProducto.css';

const ResumenProducto = ({ puntos, descuentos, url, irAlSiguienteProducto }) => {
  return (
      <div className="resultado">
          <h2>¡Enhorabuena!</h2>
          <p>Has conseguido {puntos} puntos.</p>
          <h3>¿Cómo consigo este precio?</h3>
          <p>{descuentos}</p>
          <a href={url} target="_blank" rel="noopener noreferrer">Enlace</a>

          <h3>Comparte el juego en redes sociales:</h3>
          {/* Aquí puedes añadir los íconos y enlaces para compartir en redes sociales */}

          <button onClick={irAlSiguienteProducto}>Siguiente Producto</button>
      </div>
  );
}

export default ResumenProducto;
