import React, { useState, useEffect } from 'react';
import './App.css';
import Producto from './components/Producto';
import PrecioSlider from './components/PrecioSlider';
import { haJugadoElDia } from './utils/storage';
import { cargarProductosDelDia } from './utils/carga';
import Header from './components/layout/Header';

const App = () => {
  const [productoActual, setProductoActual] = useState(null);
  const [precioEstimado, setPrecioEstimado] = useState(0);
  const fechaHoy = new Date().toISOString().slice(0, 10); // Formato: "YYYY-MM-DD"
  const [indiceProductoActual, setIndiceProductoActual] = useState(null);

  useEffect(() => {
    const indiceProducto = haJugadoElDia(fechaHoy);

    cargarProductosDelDia().then(productos => {
      if (indiceProducto !== -1 && productos.length > indiceProducto) {
        // Cargar el producto en el índice devuelto por haJugadoElDia
        setProductoActual(productos[indiceProducto]);
      } else if (indiceProducto === -1) {
        // Aquí puedes mostrar el resumen si el jugador ya ha jugado todos los productos del día
        console.log("El jugador ya ha jugado todos los productos del día. Mostrar resumen.");
      }
    });

  }, [fechaHoy]);

  return (
    <div className="App">
      <Header />
      {/* Mostrar el producto y el slider si el producto está disponible */}
      {productoActual && (
        <div>
          <Producto producto={productoActual} />
          <PrecioSlider 
            precioEstimado={precioEstimado} 
            setPrecioEstimado={setPrecioEstimado} 
            precioReal={productoActual.precio} 
          />
          {/* Aquí también puedes añadir un botón para "comprobar" y otros elementos de UI que necesites */}
        </div>
      )}
    </div>
  );
}

export default App;
