import React from 'react';
import './ResumenProducto.css';

const ResumenProducto = ({ producto, precioEstimado, puntos }) => {
    const porcentajeDiferencia = ((Math.abs(producto.precio - precioEstimado) / producto.precio) * 100).toFixed(2);
    const color = porcentajeDiferencia <= 10 ? 'green' : 'red';

  return (
    <div className="producto" style={{ borderColor: color }}>
      <img src={producto.imagen} alt={producto.nombre} className="producto-imagen" />
      <h2>{producto.nombre}</h2>
      <p>{producto.descripcion}</p>

      <h3 style={{ color }}>Precio: {producto.precio}€</h3>
      <h4>Has conseguido {puntos} Puntos!!!</h4>
      <p>Te has quedado a un {porcentajeDiferencia}% de acertar</p>

      <a href={producto.enlace} target="_blank" rel="noopener noreferrer">Si te interesa puedes encontrar el producto Aquí</a>
    </div>
  );
};

export default ResumenProducto;
