import React from 'react';

const ResumenFinal = ({ productosDelDia, puntuacionDelDia }) => {

  const generarTextoEnlace = (comercio) => {
    if (comercio === "Amazon") {
      return "Comprar ahora en Amazon.es";
    } else if (comercio === "Otros") {
      return "Comprar ahora aquí";
    } else {
      return `Comprar ahora en ${comercio}`;
    }
  }

  return (
    <div>
      <h2>Resumen del Día</h2>
      <h3>Puntuación total del día: {puntuacionDelDia}</h3>
      <ul>
        {productosDelDia.map(producto => (
          <li key={producto.id}>
            <h4>{producto.nombre}</h4>
            <img src={`/images/${producto.imagen}`} alt={producto.nombre} width={50} />
            {/* Asumo que tus imágenes están almacenadas en el directorio "ruta/de/tus/imagenes", así que deberías reemplazar esto por la ruta real de tus imágenes. */}
            <p>Puntos obtenidos: {producto.puntos}</p> 
            <a href={producto.url}>{generarTextoEnlace(producto.comercio)}</a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ResumenFinal;
