import React from 'react';
import Slider from 'react-slider';
import './PrecioSlider.css';

const determinarRango = (precio) => {
    if (precio < 50) return [0, 100];
    if (precio < 500) return [0, 1000];
    return [0, 2000];
};


const PrecioSlider = ({ precioEstimado, setPrecioEstimado, precioReal }) => {
    const [min, max] = determinarRango(precioReal);


    return (
        <div className="slider-container">
            <input
                type="number"
                value={precioEstimado}
                onChange={e => {
                    const valor = Math.min(Math.max(Number(e.target.value), min), max); // Asegura que el valor esté dentro del rango permitido
                    setPrecioEstimado(valor);
                }}
                className="precio-input"
            />
            <Slider
                value={precioEstimado}
                onChange={value => setPrecioEstimado(value)}
                min={min}
                max={max}
            />
            <div className="valores">
                <span>{min}€</span>
                <span>{max}€</span>
            </div>
        </div>
    );
};

export default PrecioSlider;
