import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import MuiInput from '@mui/material/Input';
import EuroIcon from '@mui/icons-material/Euro';

const Input = styled(MuiInput)`
  width: 70px;
`;

function determinarRango(precio) {
    if (precio < 50) return { min: 0, max: 100, step: 0.01 };
    if (precio < 500) return { min: 0, max: 1000, step: 0.01 };
    return { min: 0, max: 2000, step: 0.01 };
}

export default function InputSlider(props) {
    const { precioReal = 0 } = props;
    const { min, max, step } = determinarRango(precioReal);
    const [value, setValue] = React.useState((min + max) / 2);

    const handleSliderChange = (event, newValue) => {
        setValue(newValue);
        setInputValue(newValue.toFixed(2));
    };

    const [inputValue, setInputValue] = React.useState(value.toFixed(2));

    const handleInputChange = (event) => {
        const val = event.target.value;

        if (val === "") {
            setInputValue(val);  // Permitir una entrada vacía
            return;  // Salir temprano si la entrada está vacía
        }

        const isValidFormat = /^(\d+\.?\d{0,2}|\.\d{0,2})$/.test(val);

        if (isValidFormat) {
            setInputValue(val);  // Guardamos el valor directamente de la entrada

            const parsedValue = parseFloat(val);

            if (!isNaN(parsedValue)) {
                const clampedValue = Math.min(Math.max(parsedValue, min), max);
                setValue(clampedValue);
            }
        }
    };

    return (
        <Box sx={{ width: 500, margin: '0 auto', textAlign: 'center' }}> {/* Alineación al centro */}
            <Typography id="input-slider" gutterBottom>
                ¿Cuál es su precio?
            </Typography>
            <Grid container spacing={2} alignItems="center">
                <Grid item>
                    <EuroIcon /> {/* Icono de euro */}
                </Grid>
                <Grid item xs>
                    <Slider
                        value={typeof value === 'number' ? value : 0}
                        onChange={handleSliderChange}
                        min={min}
                        max={max}
                        step={step}
                        aria-labelledby="input-slider"
                    />
                </Grid>
                <Grid item>
                    <Input
                        value={inputValue} // Usamos el inputValue aquí en lugar de value.toFixed(2)
                        size="small"
                        onChange={handleInputChange}
                        onBlur={() => setInputValue(value.toFixed(2))} // Al perder el foco, aseguramos que tenga dos decimales
                        inputProps={{
                            step: step,
                            min: min,
                            max: max,
                            type: 'text',
                            'aria-labelledby': 'input-slider',
                        }}
                    />
                </Grid>
            </Grid>
        </Box>
    );
}
