import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/layout/Header';
import { cargarProductosDelDia } from './utils/carga';

function App() {
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    const cargarProducto = async () => {
      const productos = await cargarProductosDelDia();
      if (productos.length > 0) {
        setProducto(productos[0]); // Guarda el primer producto en el estado
      }
    };

    cargarProducto();  // Llama a la funci칩n para cargar el producto
  }, []);

  return (
    <div className="App">
      <Header />

      {/* Mostrar informaci칩n del primer producto si est치 disponible */}
      {producto && (
        <div>
          <h2>{producto.nombre}</h2>
          <img src={`/images/${producto.imagen}`} alt={producto.nombre} />
          <p>{producto.descripcion}</p>
          <p>Precio: ${producto.precio}</p>
        </div>
      )}
    </div>
  );
}

export default App;
