import React from 'react';
import { format, addMonths, isBefore, isToday, startOfMonth, endOfMonth } from 'date-fns';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './CalendarioPage.css';

const CalendarioPage = () => {
    const today = new Date();
    const startDate = new Date(2023, 8, 13); // 13 de septiembre de 2023

    const tileContent = ({ date, view }) => {
        if (view !== "month") {
            return null;  // We only want to customize the day view
        }

        let backgroundColor;
        let color;
        let link;
        const isPlayed = false;  // Aquí puedes determinar si el día ha sido jugado o no
        const isInProgress = isToday(date);

        if (isBefore(date, startDate)) {
            backgroundColor = "gray";
            color = "white"; 
        } else if (isPlayed) {
            backgroundColor = "red";
            link = `/resumen/${format(date, 'yyyy-MM-dd')}`;
        } else if (isInProgress) {
            backgroundColor = "yellow";
            color = "black";
        } else {
            backgroundColor = "green";
            link = `/jugar/${format(date, 'yyyy-MM-dd')}`;
        }

        return (
            <div style={{ backgroundColor, color, width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                {link ? <Link to={link} style={{ color }}>{format(date, 'dd')}</Link> : format(date, 'dd')}
            </div>
        );
    };

    const monthToShow = addMonths(startDate, 6); // Ejemplo para mostrar 6 meses desde la fecha de inicio
    const firstDayOfMonth = startOfMonth(monthToShow);
    const lastDayOfMonth = endOfMonth(monthToShow);

    return (
      <div>
        <Calendar 
          tileContent={tileContent}
          maxDate={lastDayOfMonth}
          minDate={firstDayOfMonth}
          activeStartDate={monthToShow}
        />
      </div>
    );
};

export default CalendarioPage;
