const fs = require('fs');

// Cambia este nombre si tu archivo tiene un nombre diferente
const filename = 'productos_2023_septiembre.json'; 

fs.readFile(filename, 'utf8', (err, data) => {
    if (err) {
        console.error(`Error leyendo el archivo: ${err}`);
        return;
    }

    let parsedData = JSON.parse(data);

    for (let dia in parsedData) {
        parsedData[dia].forEach(producto => {
            producto.comercio = "";
        });
    }

    fs.writeFile(filename, JSON.stringify(parsedData, null, 4), 'utf8', (err) => {
        if (err) {
            console.error(`Error escribiendo en el archivo: ${err}`);
            return;
        }
        console.log('Campo "comercio" añadido a cada producto exitosamente.');
    });
});
