import React from 'react';
import { format, addMonths, isBefore, isToday } from 'date-fns';
import { Link } from 'react-router-dom';

const CalendarioPage = () => {
    const today = new Date();
    const startDate = new Date(2023, 8, 13); // 13 de septiembre de 2023

    const generateMonths = () => {
        let months = [];
        for (let i = 0; i < 6; i++) {
            const monthDate = addMonths(startDate, i);
            if (isBefore(monthDate, addMonths(today, 1))) { // <-- Cambio aquí
                months.push(monthDate);
            }
        }
        return months;
    };


    const renderDays = (monthDate) => {
        const days = [];
        const month = monthDate.getMonth();

        for (let i = 1; i <= 31; i++) {
            const dayDate = new Date(monthDate.getFullYear(), month, i);
            if (dayDate.getMonth() === month) {
                days.push(dayDate);
            }
        }

        return days.map(day => {
            let color = "gray";
            let link = null;
            const isPlayed = false;
            const isInProgress = isToday(day);

            if (isBefore(day, startDate)) {
                color = "gray";
            } else if (isPlayed) {
                color = "red";
                link = `/resumen/${format(day, 'yyyy-MM-dd')}`;
            } else if (isInProgress) {
                color = "yellow";
            } else {
                color = "green";
                link = `/jugar/${format(day, 'yyyy-MM-dd')}`;
            }

            return (
                <div key={format(day, 'yyyy-MM-dd')} style={{ backgroundColor: color }}>
                    {link ? <Link to={link}>{format(day, 'dd')}</Link> : format(day, 'dd')}
                </div>
            );
        });
    };

    return (
        <div>
            {generateMonths().map(monthDate => (
                <div key={format(monthDate, 'yyyy-MM')}>
                    <h2>{format(monthDate, 'MMMM yyyy')}</h2>
                    {renderDays(monthDate)}
                </div>
            ))}
        </div>
    );
};

export default CalendarioPage;
