import React, { useState } from 'react';
import './Producto.css'; // Estilos para el componente

const Producto = ({ producto }) => {
  const [descripcionDesplegada, setDescripcionDesplegada] = useState(false);

  return (
    <div className="producto">
      <img 
        src={require(`../${producto.imagen}`).default} 
        alt={producto.nombre} 
        className="producto-imagen"
      />
      <h2>{producto.nombre}</h2>
      <button onClick={() => setDescripcionDesplegada(!descripcionDesplegada)}>
        Descripci√≥n {descripcionDesplegada ? "-" : "+"}
      </button>
      {descripcionDesplegada && <p>{producto.descripcion}</p>}
    </div>
  );
};

export default Producto;
